    # Removes all spaces und punctuation from an ISBN-10 or ISBN-13.

    # @param rawIsbn trim this number, may contain hyphens, dots or spaces
    #
    sub trim() {
        my ($rawIsbn) = @_;
        $rawIsbn =~ s{[\-\. ]}{}g;
        return uc($rawIsbn); # because of trailing "x"
    } # trim

    # Formats an ISBN-10 or ISBN-13 and inserts hyphens after the bookland (if any),
    # the country group, the publisher code and before the check digit.
    # @param rawIsbn format this number, may contain hyphens, dots or spaces
    sub format() {
        my ($rawIsbn) = @_;
        my $result = "";
        my $isbn = &trim($rawIsbn);
        my $isbn9 = "";
        my $check = "-";
        my $groupWidth = 0;
        my $rangeWidth = 0;
        my $num = 300000000;
        #          123456789
        if (0) {
        } elsif (length($isbn) == 10) {
                $isbn9 = substr($isbn, 0,  9); # without check digit
                $check = substr($isbn, 9, 1);
        } elsif (length($isbn) == 13) {
                $result .= (substring($isbn, 0, 3)); # bookland 978, 979
                $result .= ('-');
                $isbn9 = substr($isbn,  3, 9); # without bookland and check digit
                $check = substr($isbn, 12, 1);
        } else {
                $result .= ($isbn);
                $result .= (" ?WLEN");
        } # switch length
        my $test = 'test ';
            $num = ($isbn9);
            my $gindex = $GROUP_LEN; # skip [0]
            while ($gindex < scalar(@GROUPS)) {
                $test .=  "$num < " . $GROUPS[$gindex + 0] . " || $num > " . $GROUPS[$gindex + 1] . "\n";
                if ($num >= $GROUPS[$gindex + 0] && $num <= $GROUPS[$gindex + 1]) { # found
                    $groupWidth = $GROUPS[$gindex + 2];
                    my $rindex  = $GROUPS[$gindex + 3];
                    my $rcount  = $GROUPS[$gindex + 4];
                    $test .=  "[" . $rindex . "]: " . $RANGES[$rindex + 0]
                            . ", " . $RANGES[$rindex + 1] . " - " . $RANGES[$rindex + 2] ."\n";
                    while ($rcount > 0) { # loop2
                        if ($num >= $RANGES[$rindex + 0] && $num <= $RANGES[$rindex + 1]) { # found2
                            $rangeWidth = $RANGES[$rindex + 2];
                            $rcount = 0; # break loop2
                        } # found2
                        $rindex += $RANGE_LEN;
                        $rcount --;
                    } # while rcount
                    $gindex = scalar(@GROUPS); # break loop
                } # found
                $gindex += $GROUP_LEN;
            } # while gindex loop

        if ($groupWidth > 0 && $rangeWidth > 0) {
            $result .= substr($isbn9, 0, $groupWidth);
            $result .= ('-');
            $result .= substr($isbn9, $groupWidth, $rangeWidth);
            $result .= ('-');
            $result .= substr($isbn9, $groupWidth + $rangeWidth);
            $result .= ('-');
            $result .= ($check);
        } else {
            $result .= "$test: $groupWidth, $rangeWidth, $isbn9";
            $result .= ($check);
            $result .= (" ?WRNG");
        }
        return $result;
    } # format

    # Test Frame, prints all ranges for the specified country group.
    # @param args commandline arguments:
    # <ul>
    # <li>args[0] = country group (1-6 digits, default 3x = German speaking countries)
    # </ul>
    #
    # main

    while (<>) {
        s[(ISBN\s*=?\s*)([\-\d\.Xx]{10,20})][$1 . &format($2)]eg;
        print;
    } # while <>

