use strict;

	my $polar = "true";
	my $comment = "";
	my $blz = "00000000";
    while (<>) {
		if (m{\A\s*\;}) { # comments 
			if (m[(\w\w)]) {
				$comment = $1;
			}
		}
		if (m{\A\s*\[(\w+)\]}) { # section name
			$polar = ($1 eq "valid") ? "true" : "false";
		}
		s[\s][]g;
		my ($method, $list) = split(/\=/);
		if (length($method) == 1) {
			$method = "0$method";
		}
		if (length($method) == 8) {
			$blz = $method;
			$method = $comment;
		} else {
			$blz = "00000000";
		}
		if (length($method) == 2) {
			foreach my $acnt (split(/\,/, $list)) {
				print "$method\t$acnt\t$blz\t$polar\n";
			} # foreach
		}
    } # while
